import './SelectFlight.css';
import React from 'react';
import { connect } from 'react-redux';
import Flight from '../Flight/Flight';
import FlightOptions from '../FlightOptions/FlightOptions';
import * as actionTypes from '../../store/actions';

class SelectFlight extends React.Component {

    componentDidMount() {
        console.log(this.props);
    }
    componentDidUpdate() {
        console.log(this.props);
    }

    render() {
    let selFlight = "";
    if (this.props.sFlight !== null) {
        selFlight = <Flight carrier={this.props.sFlight.carrier}
                    dCity={this.props.sFlight.dCity}
                    aCity={this.props.sFlight.aCity}
                    date={this.props.sFlight.date}
                    price={this.props.sFlight.price}/>
    }
    let userOptions = <FlightOptions 
                passengers={this.props.options.passengers}
                bags={this.props.options.bags}
                carryOn={this.props.options.carryOn}
                class={this.props.options.class}
                price={this.props.sFlight.price}
                />
    /* let userOptions = us.map(item => {
        return <FlightOptions 
                    passengers={item.passengers}
                    bags={item.bags}
                    carryOn={item.carryOn}
                    class={item.class}/>
    }) */

    return (
        <div className="SelectFlightContainer">
            {selFlight}
            <div className="controls">
                <div>Passengers</div>
                <div>Bags</div>
                <div>Carry-Ons</div>
                <div>Class</div>
                <div>Price</div>
            </div>
            <div className="controls2">
                {userOptions}
            </div>
            <div className="controls3">
                <div className="btns-container">
                    <button className="increment-btn" onClick={() => this.props.onPassengerIncrement()}>+</button>
                    <button className="decrement-btn" onClick={() => this.props.onPassengerDecrement()}>-</button>
                </div>
                <div className="btns-container">
                    <button className="increment-btn" onClick={() => this.props.onBagIncrement()}>+</button>
                    <button className="decrement-btn" onClick={() => this.props.onBagDecrement()}>-</button>
                </div>
                <div className="btns-container">
                    <button className="increment-btn" onClick={() => this.props.onCarryOnIncrement()}>+</button>
                    <button className="decrement-btn" onClick={() => this.props.onCarryOnDecrement()}>-</button>
                </div>
                <select>
                    <option>Coach</option>
                    <option onClick={() => this.props.onSetClass("Second Class")}>Second Class</option>
                    <option>First Class</option>
                </select>
                <div>x</div>
            </div>
            <button className="Bookbtn">Book</button>
        </div>
    )
    }
}

const mapStateToProps = state => {
    return {
        sFlight: state.selectedFlight,
        options: state.options
    }
}

const mapDispatchToProps = dispatch => {
    return {
        onPassengerIncrement: () => dispatch({type: actionTypes.PASSENGER_INCREMENT}),
        onPassengerDecrement: () => dispatch({type: actionTypes.PASSENGER_DECREMENT}),
        onBagIncrement: () => dispatch({type: actionTypes.BAG_INCREMENT}),
        onBagDecrement: () => dispatch({type: actionTypes.BAG_DECREMENT}),
        onCarryOnIncrement: () => dispatch({type: actionTypes.CARRYON_INCREMENT}),
        onCarryOnDecrement: () => dispatch({type: actionTypes.CARRYON_DECREMENT}),
        onSetClass: (val) => dispatch({type: actionTypes.SET_CLASS, val})
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(SelectFlight);
