import React from 'react';
import './BookedFlights.css';
import BookedFlight from '../../components/BookedFlight/BookedFlight';
import { connect } from 'react-redux';
import * as actionTypes from '../../store/actions';
import Modal from '../../components/Modal/Modal';

class BookedFlights extends React.Component {
    /* state = {
        modalVisible: false
    }

    setModalVisible() {
        this.setState({modalVisible: !this.state.modalVisible});
    } */

    render() {

        let modal = "";
        if (this.state.modalVisible) {
            modal = <Modal />
        }


        return (
            <div className="BookedFlights">
                {modal}
                <h2>Booked Flights</h2>
                <div className="bookedflights-list">
                    <ul>
                        <li>Carrier</li>
                        <li>Departure</li>
                        <li>Arrival</li>
                        <li>Date</li>
                        <li>Passengers</li>
                        <li>Bags</li>
                        <li>Carry-Ons</li>
                        <li>Price</li>
                    </ul>
                    <br />
                    {this.props.bFlights.map((el, i) => {
                        return <BookedFlight 
                                    carrier={el.carrier}
                                    dCity={el.dCity}
                                    aCity={el.aCity}
                                    date={el.date}
                                    passengers={el.passengers}
                                    bags={el.bags}
                                    carryOn={el.carryOn}
                                    totalPrice={el.totalPrice}
                                    key={el.id}
                                    /* click={() => this.props.onRemoveFlight(el.id)}*/
                                    click={() => this.setModalVisible()}
                                    />
                    })}
                </div>
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
        bFlights: state.bookedFlights
    }
}
const mapDispatchToProps = dispatch => {
    return {
        onRemoveFlight: (removeId) => dispatch({type: actionTypes.REMOVE_BOOKED_FLIGHT, removeId})
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(BookedFlights);
