import * as actionTypes from './actions';

const initialState = {
    selectedFlight: {
        carrier: "",
        dCity: "",
        aCity: "",
        date: "",
        price: ""
    },
    bookedFlights: []
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case actionTypes.ADD_FLIGHT: 
            const addFlight = {
                carrier: action.carrier,
                dCity: action.dCity,
                aCity: action.aCity,
                date: action.date,
                price: action.price
            }
            return {
                ...state,
                selectedFlight: addFlight
            }
    }
    return state;
}

export default reducer;
