import TopBar from './components/TopBar/TopBar';
import './App.css';
import FlightSelector from './containers/FlightSelector/FlightSelector';
import BookedFlights from './containers/BookedFlights/BookedFlights';
import { connect } from 'react-redux';
import * as actionTypes from './store/actions';
import React from 'react';
import Modal from './components/Modal/Modal';

class App extends React.Component {
    state = {
        modalVisible: false,
        removeFlight: false,
        bookFlight: false
    }

    setModalVisible(type) {
      if (type === "Remove") {
        this.setState({removeFlight: !this.state.removeFlight});
      } else {
        this.setState({bookFlight: !this.state.bookFlight})
      }
        this.setState({modalVisible: !this.state.modalVisible});
    }

  /* useEffect(() => {
    dispatch.onInitFlights(flights);
  }, []); */
    render() {

      let confirmFlightMsg = <p>Book this Flight?</p>;
      let removeBookedFlightMsg = <p>Are you sure you want to remove flight?</p>;

      let modal = "";
      if (this.state.modalVisible && this.state.removeFlight) {
          modal = <Modal message={removeBookedFlightMsg} click={this.props.onRemoveFlight()}/>
      }


      return (
        <div className="App">
          {modal}
          <TopBar />
          <FlightSelector />
          <hr />
          <BookedFlights click={() => this.setModalVisible("Remove")}/>
        </div>
      );
    }


}

const mapStateToProps = state => {
  return {
      avFlights: state.availableFlights
  };
};

const mapDispatchToProps = dispatch => {
  return {
    onInitFlights: flights => dispatch({type: actionTypes.INIT_FLIGHTS, flights: flights}),
    onRemoveFlight: (removeId) => dispatch({type: actionTypes.REMOVE_BOOKED_FLIGHT, removeId})
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(App);
