import * as actionTypes from './actions';

const initialState = {
    availableFlights: [
        {carrier: "SW", dCity: "LA", aCity: "Lihue", date: "1/1", price: "$800"},
        {carrier: "AM", dCity: "CHI", aCity: "DEN", date: "1/5", price: "$800"},
        {carrier: "SW", dCity: "LA", aCity: "Lihue", date: "1/1", price: "$700"},
        {carrier: "SW", dCity: "LA", aCity: "Lihue", date: "1/1", price: "$800"},
        {carrier: "SW", dCity: "LA", aCity: "Lihue", date: "1/1", price: "$800"}
      ],
    selectedFlight: {},
    options: {passengers: 1, bags: 0, carryOn: 0, class: "coach"},
    bookedFlights: []
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        /*case actionTypes.INIT_FLIGHTS:
            const avFlights = [];
            action.flights.map((ob, i) => {
                return avFlights[i] = ob;
            })
            return {
                ...state,
                availableFligts: avFlights
            }*/
        case actionTypes.ADD_FLIGHT: 
            const addFlight = {
                carrier: action.flightData.carrier,
                dCity: action.flightData.dCity,
                aCity: action.flightData.aCity,
                date: action.flightData.date,
                price: action.flightData.price
            }
            return {
                ...state,
                selectedFlight: addFlight
            }
        case actionTypes.REMOVE_FLIGHT:
            return {
                ...state,
                selectedFlight: {}
            }
        case actionTypes.PASSENGER_INCREMENT:
            let p = state.options.passengers;
            let bags = state.options.bags;
            let carryOn = state.options.carryOn;
            let c = state.options.class;
            p += 1;
            return {
                ...state,
                options: {passengers: p, bags: bags, carryOn: carryOn, class: c}
            }
        case actionTypes.PASSENGER_DECREMENT:
            let values = Object.values(state.options);
            console.log(values);
            values[1] -= 1;
            return {
                ...state,
                options: {passengers: values[0], bags: values[1], carryOn: values[2], class: values[3]}
            }
    }
    return state;
}

export default reducer;
