import './SelectFlight.css';
import React from 'react';
import { connect } from 'react-redux';
import Flight from '../Flight/Flight';
import { ADD_FLIGHT } from '../../store/actions';

const SelectFlight = (props) => {
    let selFlight = "";
    if (props.state.sFlight !== null) {
        return <Flight carrier={sFlight.carrier}
                    dCity={sFlight.dCity}
                    aCity={sFlight.aCity}
                    date={sFlight.date}
                    price={sFlight.price}/>
    }

    return (
        <div className="SelectFlightContainer">
            {selFlight}
            <div className="controls">
                <div>Passengers</div>
                <div>Bags</div>
                <div>Carry-Ons</div>
                <div>Class</div>
                <div>Price</div>
            </div>
            <button>Book</button>
        </div>
    )
}

const mapStateToProps = state => {
    return {
        sFlight: state.selectedFlight
    }
}

export default connect(mapStateToProps)(SelectFlight);
