import React from 'react';
import './AvailableFlights.css';
import { connect } from 'react-redux';
import Flight from '../Flight/Flight';

class AvailableFlights extends React.Component {

    selectFlightHandler() {

    }

    render() {
        return (
            <div className="AvFlightsContainer">
                <ul>
                    <li>Carrier</li>
                    <li>Depature City</li>
                    <li>Arrival City</li>
                    <li>Date</li>
                    <li>Price</li>
                </ul>
                {this.props.avFlights.map((flight, i) => {
                    return <Flight 
                            carrier={flight.carrier}
                            dCity={flight.dCity}
                            aCity={flight.aCity}
                            date={flight.date}
                            price={flight.price}
                            key={i}
                            onClick={this.props.onSelectFlight(flight.carrier, flight.dCity, flight.aCity, flight.date, flight.price)}/>
                    })}
            </div>
        )
    }

}

const mapStateToProps = state => {
    return {
        avFlights: state.availableFlights,
        selFlight: state.selectedFlight
    }
}

const mapDispatchToProps = dispatch => {
    return {
        onSelectFlight: (carrier, dCity, aCity, date, price) => dispatch({type: actionTypes.ADD_FLIGHT, flightData: {carrier: carrier, dCity: dCity, aCity: aCity, date: date, price: price} }),
        onRemoveFlight: () => dispatch({type: actionTypes.REMOVE_FLIGHT})
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(AvailableFlights);
