import * as actionTypes from './actions';

const initialState = {
    availableFlights: [],
    selectedFlight: {
        carrier: "",
        dCity: "",
        aCity: "",
        date: "",
        price: ""
    },
    bookedFlights: []
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case actionTypes.INIT_FLIGHTS:
            const avFlights = [];
            action.flights.map((ob, i) => {
                avFlights[i] = ob;
            })
            return {
                ...state,
                availableFligts: avFlights
            }
        case actionTypes.ADD_FLIGHT: 
            const addFlight = {
                carrier: action.carrier,
                dCity: action.dCity,
                aCity: action.aCity,
                date: action.date,
                price: action.price
            }
            return {
                ...state,
                selectedFlight: addFlight
            }
        case actionTypes.REMOVE_FLIGHT:
            return {
                ...state,
                selectedFlight: {}
            }
    }
    return state;
}

export default reducer;
